import axios from 'axios';
import { updateFormValue } from '../index';

const registerFormHandler = async (dispatch, getState) => {
  dispatch(updateFormValue('registerForm', { loading: true }));
  const { form } = getState();
  const {
    firstName,
    middleName,
    lastName,
    email,
    password,
    confirmPassword,
  } = form.registerForm;
  if (firstName === '' || lastName === '' || email === '' || password === '' || confirmPassword === '') {
    return dispatch(updateFormValue('registerForm', { loading: false, error: 'Please fill all the fields.' }));
  }
  if (password !== confirmPassword) {
    return dispatch(updateFormValue('registerForm', { loading: false, error: "Password didn't match" }));
  }
  try {
    const res = await axios.post('http://localhost:3000/user/',
      {
        firstName,
        middleName,
        lastName,
        email,
        password,
        confirmPassword,
      });
    if (res.status === 200) {
      dispatch(updateFormValue('registerForm',
        {
          firstName: '',
          middleName: '',
          lastName: '',
          email: '',
          password: '',
          confirmPassword: '',
          loading: false,
          error: null,
          success: true,
        }));
    }
  } catch {
    dispatch(updateFormValue('registerForm', { loading: false, error: 'Could not Register. Please try again.' }));
  }
};

export default registerFormHandler;
