import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { Form } from '../../../../common';
import registerForm from './structure';
import { register } from '../helper-functions';
import { passwordStrength, emailValidator } from '../utility-functions';

class Register extends Component {
  state = {
    // this represents the completion of the registration process
    complete: false,
    // it holds the notification details for the next component to be rendered.
    notification: '',
  };

  handleRegistration = async (data) => {
    if (!emailValidator(data.email)) {
      return { error: 'Please enter a valid email' };
    }
    if (passwordStrength(data.password) === 'weak') {
      return { error: 'Please enter a stronger password' };
    }
    if (data.password !== data.confirmPassword) {
      return { error: "Passwords don't match" };
    }
    const res = await register(data);
    this.setState({
      complete: true,
      notification: {
        message: 'Please verify your email',
        intent: 'success',
      },
    });
    return res;
  }

  render() {
    const { complete, notification } = this.state;
    return (
      <div>
        {complete ? <Redirect notification={notification} to="/login" />
          : <Form data={registerForm} callback={this.handleRegistration} />
        }
      </div>
    );
  }
}

export default Register;
