import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { Position, Toast, Toaster } from "@blueprintjs/core";

class VerifyEmail extends React.Component {
  state={
    emailVerification: null,
    loading: true,
  };

  async componentWillMount() {
    const { match } = this.props;
    try {
      const res = await axios.get(`http://192.168.1.66:4001/auth/email-verification?token=${match.params.token}`);
      if (res.status === 200) {
        this.setState({ emailVerification: true });
      }
    } catch (e) {
      this.setState({ emailVerification: false });
    }
  }

  render() {
    // console.log('emailVerification status', this.state.emailVerification);
    return (
      <div>
        <h1>Email verification Component goes here</h1>
        <Toaster position={Position.TOP}>
          <Toast message="You have been verified" intent="success" />
        </Toaster>
      </div>
    );
  }
}

VerifyEmail.propTypes = {
  match: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default VerifyEmail;
