import axios from 'axios';
import { updateFormValue } from '../index';

export default async (dispatch, getState) => {
  dispatch(updateFormValue('loginForm', { loading: true }));
  const { form } = getState();
  const { email, password } = form.loginForm;
  if (email === '' || password === '') {
    return dispatch(updateFormValue('loginForm', { loading: false, error: 'Enter your login credentials.' }));
  }
  try {
    const res = await axios.post('http://localhost:3000/user', { email, password });
    const { data } = res;
    if (res.status === 200 && data.token) {
      dispatch(updateFormValue('loginForm', {
        loading: false,
        error: null,
        success: true,
        email: '',
        password: '',
      }));
      sessionStorage.setItem('USER_ID', data.id);
      sessionStorage.setItem('SESSION_ID', data.token);
      sessionStorage.setItem('EMAIL', data.email);
      sessionStorage.setItem('USER_TYPE', data.type);
    }
  } catch {
    dispatch(updateFormValue('loginForm', { loading: false, error: 'Invalid username/password' }));
  }
  // console.log(res);
};
