import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import Task from './Task';
import NewTask from './NewTask';

class Column extends Component {
  state = {
    tasks: [],
  };

  componentWillMount() {
    this.setTasksInState();
  }

  componentDidUpdate() {
    this.setTasksInState();
  }

  setTasksInState = () => {
    const { database, columnId } = this.props;
    const tasks = database.BoardColumnCard.allIds.filter((obj) => {
      if (database.BoardColumnCard.byId[obj].boardColumnId === columnId) {
        return obj;
      }
    }).reduce((obj, e) => {
      return database.BoardColumnCard.byId[e];
    }, []);
    console.log(tasks);
    // this.setState({
    //   tasks,
    // });
  }

  render() {
    const { column, columnId, index } = this.props;
    const { tasks } = this.state;
    return (
      <Draggable
        draggableId={column.id}
        index={index}
      >
        {provided => (
          <div
            className="column-container"
            ref={provided.innerRef}
            {...provided.draggableProps}
          >
            <div className="column-inner-container">
              <div
                className="column-title"
                {...provided.dragHandleProps}
              >
                <span>
                  {column.name}
                </span>
              </div>
              <Droppable
                droppableId={column.id}
                type="task"
              >
                {provided => (
                  <div
                    className="task-list"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    {tasks.map((task, index) => <Task key={task.id} task={task} index={index} />)}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
              {provided.placeholder}
              <NewTask columnId={column.id} />
            </div>
          </div>
        )}
      </Draggable>
    );
  }
}

Column.propTypes = {
  column: PropTypes.objectOf(PropTypes.any).isRequired,
  tasks: PropTypes.arrayOf(PropTypes.any).isRequired,
  index: PropTypes.number.isRequired,
};

const mapStateToProps = state => state;
export default connect(mapStateToProps)(Column);
