import React from 'react';
import { Button, Intent, Tooltip } from '@blueprintjs/core';
import client from '../../../../socket';
import { callHandler, onOfferHandler, main } from './helper-functions';

class Index extends React.Component {
  state={ apis: null, pc: null };

  async componentWillMount() {
    const apisRes = await client.scope('Mentee');
    this.setState({ apis: apisRes });
    await this.socketEventListner();
  }

  socketEventListner = async () => {
    const { apis } = this.state;
    const pc = await main(apis);
    this.setState({ pc });
  }

  _callHandler = async (apis) => {
    const { pc } = this.state;
    pc.createOffer('video');
  }

  render() {
    const { apis } = this.state;
    return (
      <div style={{ marginTop: 100, marginLeft: 100 }}>
        {/* eslint-disable-next-line */}
        <video controls id="video1" playsInline autoPlay style={{ height: 300, width: 400, background: 'black'}} />
        <div style={{ marginTop: 5 }}>
          <Tooltip content="Audio Call" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="phone" intent={Intent.SUCCESS} />
          </Tooltip>
          <Tooltip content="Video Call" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="mobile-video" intent={Intent.SUCCESS} />
          </Tooltip>
          <Tooltip content="Chatting" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="chat" intent={Intent.SUCCESS} />
          </Tooltip>
          <Tooltip content="Recording" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="record" intent={Intent.SUCCESS} />
          </Tooltip>
          <Tooltip content="Screen Sharing" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="duplicate" intent={Intent.SUCCESS} />
          </Tooltip>
          <Tooltip content="Screen Sharing" position="top">
            <Button onClick={() => this._callHandler(apis)} text="" style={{ marginLeft: 10 }} icon="headset" intent={Intent.SUCCESS} />
          </Tooltip>

        </div>
      </div>
    );
  }
}
export default Index;
