import { UPDATE_FORM_VALUE } from './types';

export const updateFormValue = (schema, data) => (
  {
    type: UPDATE_FORM_VALUE,
    payload: { schema, data },
  }
);

export const mainFormHandler = (schema, data) => {
  return (dispatch, getState) => {
    dispatch(updateFormValue(schema, data));
  };
};
