export const basicForm = [
  {
    id: 'email',
    fieldType: 'input',
    name: 'Email',
    placeholder: 'Eg, alex@gmail.com',
    icon: {
      side: 'right',
      name: 'eye-open',
    },
  },
  {
    id: 'password',
    fieldType: 'input',
    hidden: true,
    name: 'Password',
    placeholder: 'Eg, ........',
    icon: {
      side: 'right',
      name: 'eye-open',
    },
  },
  {
    id: 'submit',
    fieldType: 'button',
    text: 'Submit',
    fill: 'fill',
    intent: 'primary',
    large: 'large',
  },
  { error: null },
  { success: null },
  { loading: false },
];

export const registerForm = {
  firstName: '',
  middleName: '',
  lastName: '',
  email: '',
  password: '',
  confirmPassword: '',
  error: null,
  success: null,
  loading: false,
};
