export const basicForm = [
  {
    id: 'email',
    fieldType: 'input',
    name: 'Email',
    placeholder: 'Eg, alex@gmail.com',
  },
  {
    id: 'password',
    fieldType: 'input',
    hidden: true,
    name: 'Password',
    placeholder: 'Eg, ........',
    // icon: 'lock',
  },
  {
    id: 'submit',
    fieldType: 'button',
    text: 'Submit',
  },
  { error: null },
  { success: null },
  { loading: false },
];

export const registerForm = {
  firstName: '',
  middleName: '',
  lastName: '',
  email: '',
  password: '',
  confirmPassword: '',
  error: null,
  success: null,
  loading: false,
};
