import React, { Component } from 'react';

class Blogs extends Component {
  state = {};

  onClick = (e) => {
    document.execCommand('bold', false, '');
  }

  render() {
    return (
      <div className="blogs">
        <h1>Blogs</h1>
        <button type="button" className="tool-items fa fa-bold" onClick={this.onClick}>
          Bold
        </button>
        <div>
          <h2>Preview here</h2>
          <div
            contentEditable
            style={{ minWidth: '100px', minHeight: '100px' }}
          />
        </div>
      </div>
    );
  }
}

export default Blogs;
