import { onIceConnectionStateChange } from '../common';
import client from '../../../../../../socket';
import onIceCandidate from './onIceCandidate';

export default async (apis) => {
  const server = {"iceServers":[{"url":"stun:stun.l.google.com:19302"}]};;

  const offerOptions = {
    offerToRecieveAudio: 1,
    offerToRecieveVideo: 1,
  };

  // creating new RTCPeerConnetion
  // TODO server configuration for TURN or STUN
  const pc = new RTCPeerConnection(server);

  // adding some listener to track connetion state
  pc.addEventListener('icecandidate', e => onIceCandidate(e, pc, apis));
  pc.addEventListener('iceconnectionstatechange', e => onIceConnectionStateChange(e, pc));

  // geting local audio and video
  const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });

  // adding video and audio  track to pc
  stream.getTracks().forEach(track => pc.addTrack(track, stream));

  // creating offer for remote user
  try {
    const offer = await pc.createOffer(offerOptions);
    await pc.setLocalDescription(offer);
    await apis.createOffer([offer, [{ userId: 3 }]]);
    client.on('answer', (data) => {
      console.log('On Aswer handler called', data);
      pc.setRemoteDescription(data);
    });

    client.on('icecandidate', (data) => {
      console.log('icecandidate handler called', data);
      pc.addIceCandidate(data.candidate);
    });
  } catch (e) {
    console.error('CreateOffer Error', e);
  }
  return pc;
};
