import React, { Component } from 'react';
import { Button, TextArea } from '@blueprintjs/core';

class Blogs extends Component {
  state = {};

  onClick = (type) => {
    // eslint-disable-next-line no-undef
    document.execCommand(type, false, '');
    // eslint-disable-next-line no-undef
    document.getElementById('editor').focus();
  }

  contentChange = (e) => {
    const content = e.target.innerText;
    if (content === '\n' || content === '') {
      e.target.innerHTML = '';
    }
  }

  onEditorFocus = (e) => {
    const editableDiv = e.target;
    let caretPos = 0;
    let sel;
    let range;
    if (window.getSelection) {
      sel = window.getSelection();
      if (sel.rangeCount) {
        range = sel.getRangeAt(0);
        if (range.commonAncestorContainer.parentNode === editableDiv) {
          caretPos = range.endOffset;
        }
      }
    } else if (document.selection && document.selection.createRange) {
      console.log('selection');
      range = document.selection.createRange();
      if (range.parentElement() === editableDiv) {
        const tempEl = document.createElement('span');
        editableDiv.insertBefore(tempEl, editableDiv.firstChild);
        const tempRange = range.duplicate();
        tempRange.moveToElementText(tempEl);
        tempRange.setEndPoint('EndToEnd', range);
        caretPos = tempRange.text.length;
      }
    }
    return caretPos;
  }

  render() {
    return (
      <div className="blogs">
        <h1>Blogs</h1>
        <Button
          type="button"
          text="bold"
          onClick={() => this.onClick('bold')}
        />
        <Button
          type="button"
          text="Italic"
          onClick={() => this.onClick('italic')}
        />
        <Button
          type="button"
          text="Underline"
          onClick={() => this.onClick('underline')}
        />
        <div>
          <TextArea
            fill
            placeholder="Headline"
            maxLength="150"
            rows="1"
            style={{
              resize: 'none',
              overflowX: 'hidden',
              overflowWrap: 'break-word',
              maxHeight: '97px',
              fontSize: '30px',
            }}
          />
          <div
            id="editor"
            role="textbox"
            tabIndex={0}
            contentEditable
            datatext="Start Writing ..."
            style={{ content: 'attr(placeholder)', minWidth: '100px', minHeight: '100px' }}
            onKeyUp={this.contentChange}
            onKeyDown={this.contentChange}
            onFocus={this.onEditorFocus}
            onClick={this.onEditorFocus}
          />
        </div>
      </div>
    );
  }
}

export default Blogs;
