import { onIceCandidate, onIceConnectionStateChange } from '../common';


function gotRemoteStream(e) {
  const videoElement = document.getElementById('video1');
  if (videoElement.srcObject !== e.streams[0]) {
    videoElement.srcObject = e.streams[0];
  }
}

export default async function onOfferHandler(data, apis) {
  const server = null;
  // creating new RTCPeerConnetion
  // TODO server configuration for TURN or STUN
  const pc = new RTCPeerConnection(server);

  // adding some listener to track connetion state
  pc.addEventListener('icecandidate', e => onIceCandidate(e, pc, apis));
  pc.addEventListener('iceconnectionstatechange', e => onIceConnectionStateChange(e, pc));
  pc.setRemoteDescription(data);
  pc.addEventListener('track', gotRemoteStream);
  try {
    const answer = await pc.createAnswer();
    pc.setLocalDescription(answer);
    apis.createAnswer(answer, [{ userId: 2 }]);
  } catch (e) {
    console.error('Error in Create Answer', e);
  }
}
