import React, { Component } from 'react';
import { Button, TextArea } from '@blueprintjs/core';

class Blogs extends Component {
  state = { contentClass: 'empty' };

  onClick = (type) => {
    // eslint-disable-next-line no-undef
    document.execCommand(type, false, '');
  }

  contentChange = (e) => {
    const { contentClass } = this.state;
    const content = e.target.innerText;
    if (content === '\n' || content === '') {
      e.target.innerText = '';
      if (contentClass !== 'empty') {
        this.setState({ contentClass: 'empty' });
      }
    } else if (contentClass !== 'non-empty') {
      this.setState({ contentClass: 'non-empty' });
    }
  }

  render() {
    const { contentClass } = this.state;
    return (
      <div className="blogs">
        <h1>Blogs</h1>
        <Button
          type="button"
          text="bold"
          onClick={() => this.onClick('bold')}
        />
        <Button
          type="button"
          text="Italic"
          onClick={() => this.onClick('italic')}
        />
        <Button
          type="button"
          text="Underline"
          onClick={() => this.onClick('underline')}
        />
        <div>
          <TextArea
            fill
            placeholder="Headline"
            maxLength="150"
            rows="1"
            style={{
              resize: 'none',
              overflowX: 'hidden',
              overflowWrap: 'break-word',
              maxHeight: '97px',
              fontSize: '30px',
            }}
          />
          <div
            role="textbox"
            tabIndex={0}
            className={contentClass}
            contentEditable
            datatext="Start Writing ..."
            style={{ content: 'attr(placeholder)', minWidth: '100px', minHeight: '100px' }}
            onKeyUp={this.contentChange}
            onKeyDownCapture={this.contentChange}
          />
        </div>
      </div>
    );
  }
}

export default Blogs;
