import client from '../../../../../../socket';
import { onIceConnectionStateChange } from '../common';
import onIceCandidateHandler from './onIceCandidate';

function gotRemoteStream(e) {
  const videoElement = document.getElementById('video1');
  if (videoElement.srcObject !== e.streams[0]) {
    videoElement.srcObject = e.streams[0];
  }
}

const mediaSelector = async (mediaType) => {
  let stream = null;
  switch (mediaType) {
    case 'audio':
      stream = await navigator.mediaDevices.getUserMedia({ video: false, audio: true });
      return stream;
    case 'video':
      stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      return stream;
    case 'screenshare':
      stream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      return stream;
    default:
      return stream;
  }
};

export default async (apis, offerHandler) => {
  // server configuration
  const server = null;

  const offerOptions = {
    offerToRecieveAudio: 1,
    offerToRecieveVideo: 1,
  };

  // Initialize PeerConnection
  const pc = new RTCPeerConnection(server);

  // Adding icecandidate listner
  pc.addEventListener('icecandidate', e => onIceCandidateHandler(e, pc, apis));

  // Adding IceConnection State Change
  pc.addEventListener('iceconnectionstatechange', e => onIceConnectionStateChange(e, pc));

  // Adding Ontrack listner
  pc.addEventListener('track', gotRemoteStream);

  // creating offer for list of users
  const createOffer = async (mediaType) => {

    const stream = await mediaSelector(mediaType);
    console.log('get Video mediaSelector', stream);
    stream.getTracks().forEach(track => pc.addTrack(track, stream));

    const offer = await pc.createOffer(offerOptions);
    await pc.setLocalDescription(offer);
    await apis.createOffer([offer, [{ userId: 3 }]]);
  };

  client.on('answer', (data) => {
    console.log('On Aswer handler called', data);
    pc.setRemoteDescription(data);
  });

  client.on('offer', async (data) => {
    console.log('offer arived', data);
    // const { users, offerData } = data;
    // delete data.users;

    pc.setRemoteDescription(data);
    const answer = await pc.createAnswer();
    pc.setLocalDescription(answer);
    apis.createAnswer([answer, [{ userId: 2 }]]);
  });

  client.on('icecandidate', (data) => {
    console.log('icecandidate handler called', data);
    pc.addIceCandidate(new RTCIceCandidate(JSON.parse(data)));
  });

  return { pc, createOffer };
};
