import React, { Component } from 'react';

class Blogs extends Component {
  state = {
    marked: '',
  }

  onInput = (e) => {
    console.log(e);
    this.setState({
      marked: e.value,
    });
  };

  render() {
    const { marked } = this.state;
    return (
      <div className="blogs">
        <h1>Blogs</h1>
        <div>
          <h2>Write Here</h2>
          <input type="textarea" value={marked} onInput={(e) => this.onInput(e)} />
        </div>
        <div>
          <h2>Preview here</h2>
        </div>
      </div>
    );
  }
}

export default Blogs;
