export const basicForm = [
  {
    id: 'email',
    fieldtype: 'input',
    name: 'Email',
    placeholder: 'Eg, alex@gmail.com',
  },
  {
    id: 'password',
    fieldtype: 'input',
    hidden: true,
    name: 'Password',
    placeholder: 'Eg, ........',
    icon: {
      side: 'right',
      name: 'eye-open',
    },
  },
  {
    id: 'submit',
    fieldtype: 'button',
    text: 'Submit',
    fill: 'fill',
    intent: 'primary',
    large: 'large',
  },
];

export const registerForm = {
  firstName: '',
  middleName: '',
  lastName: '',
  email: '',
  password: '',
  confirmPassword: '',
  error: null,
  success: null,
  loading: false,
};
