import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import * as actions from '../../actions';
import client from '../../socket';
import { PopoverForm } from '../../components';
import TaskFormStructure from './structure';

class NewTask extends Component {
  state = {
    // if to open the popover to add the title to the new task.
    popOpen: false,
    api: {},
  };

  async componentWillMount() {
    const api = await client.scope('Mentee');
    this.setState({
      api,
    });
  }

  handlePopOverForm = () => {
    const { popOpen } = this.state;
    this.setState({
      popOpen: !popOpen,
    });
  }

  addNewTask = (data) => {
    const { columnId } = this.props;
    console.log(data, columnId);
    // return { response: 200 };
  };

  render() {
    const { popOpen } = this.state;
    return (
      <div>
        {
          popOpen
          && (
          <PopoverForm
            isOpen={popOpen}
            onClose={this.handlePopOverForm}
            callback={this.addNewTask}
            structure={TaskFormStructure}
          />
          )
        }
        <div className="column-footer" role="button" tabIndex={0} onKeyDown={this.onClick} onClick={this.handlePopOverForm}>
          + New Task
        </div>
      </div>
    );
  }
}

NewTask.propTypes = {
  columnId: PropTypes.string.isRequired,
};

const mapStateToProps = (state, ownprops) => ({ ...state, ...ownprops });
export default connect(mapStateToProps, { ...actions })(NewTask);
