import { UPDATE_CLASS_VALUE } from '../actions/types';

const initialState = {
  tasks: {
    'task-1': {
      id: 'task-1',
      title: 'Sample Task head',
      description: 'Sample Task here',
    },
  },
  columns: {
    'column-1': {
      id: 'column-1',
      title: 'todo',
      taskIds: ['task-1'],
    },
  },
  columnOrder: ['column-1'],
  overLayContent: {
    isOpen: false,
    taskId: '',
    taskTitle: '',
    taskDescription: '',
  },
};

export default (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_CLASS_VALUE:
      return {
        ...state,
        [action.payload.schema]: action.payload.data,
      };
    default:
      return state;
  }
};
