import React from 'react';
import { Button } from '@blueprintjs/core';
import client from '../../../../socket';
import { createOffer } from './helper-functions';

class Index extends React.Component {
  state={ apis: null, localStream: null };

  async componentWillMount() {
    const apisRes = await client.scope('Mentee');
    this.setState({ apis: apisRes });
    this.socketEventListner();
  }

  socketEventListner = () => {
    client.on('createOffer', function CreateOffer(data) {
      console.log('create Offer is available in main socket', data, this);
    });
  }

  render() {
    const { apis, localStream } = this.state;
    return (
      <div style={{ marginTop: 100, marginLeft: 100 }}>
        {/* eslint-disable-next-line */}
        <video src={localStream} playsInline autoPlay style={{ height: 300, width: 400, background: 'black'}} />
        <div style={{ marginTop: 20 }}>
          <Button onClick={this.startVideo} text="Start" style={{ margin: 10 }} />
          <Button onClick={this.remoteCall} text="Call " style={{ margin: 10 }} />
          <Button onClick={() => createOffer(apis)} text="Create Offer" style={{ margin: 10 }} />
          <Button onClick={this.createAnswer} text="Create Answer" style={{ margin: 10 }} />
          <Button onClick={this.addCandidate} text="Add Condidate" style={{ margin: 10 }} />
        </div>
      </div>
    );
  }
}
export default Index;
