import React from 'react';
import PropTypes from 'prop-types';
import { InputGroup, Icon, Label, Tooltip } from '@blueprintjs/core';
import { IconNames } from '@blueprintjs/icons';

class CustomInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hide: true };
  }

  render() {
    const { placeholder, iconName, class_, label_ } = this.props;
    if (iconName === 'Search') {
      const icon = <Icon icon={IconNames.SEARCH} iconSize={Icon.SIZE_LARGE} />;
      return (
        <Label>
          <span className="label-text">{label_}</span>
          <InputGroup
            placeholder={placeholder}
            className={class_}
            leftIcon={icon}
            large
          />
        </Label>
      );
    }
    if (iconName === 'Lock') {
      const rightIcon = <Icon icon={IconNames.LOCK} iconSize="15" />;
      return (
        <Label>
          <span className="label-text">{label_}</span>
          <InputGroup
            placeholder={placeholder}
            className={class_}
            rightElement={rightIcon}
            large
          />
        </Label>
      );
    }

    return (
      <Label>
        <span className="label-text">{label_}</span>
        <InputGroup
          placeholder={placeholder}
          className={class_}
          large
        />
      </Label>
    );
  }
}
CustomInput.defaultProps = {
  class_: 'input',
};
CustomInput.propTypes = {
  placeholder: PropTypes.string.isRequired,
  iconName: PropTypes.string.isRequired,
  class_: PropTypes.string,
  label_: PropTypes.string.isRequired,
};
export default CustomInput;
