import React from 'react';
import PropTypes from 'prop-types';
import { InputGroup, Icon, Label } from '@blueprintjs/core';
import { IconNames } from '@blueprintjs/icons';

const Lock = (iconName, handleLockClick) => {
  if (iconName === 'Search') {
    return (
      { leftIcon: <Icon icon={IconNames.SEARCH} iconSize={Icon.SIZE_LARGE} />, rightIcon: null }
    );
  }
  if (iconName === 'Lock') {
    return (
      { leftIcon: null, rightIcon: <Icon icon={IconNames.EYE_OPEN} iconSize="25" style={{ top: 1 }} onClick={handleLockClick} /> }
    );
  }
  return ({ leftIcon: null, rightIcon: null });
};

class CustomInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = { showPassword: false };
  }

  handleLockClick = () => {
    const { showPassword } = this.state;
    this.setState({ showPassword: !showPassword });
  }

  render() {
    const { placeholder, iconName, class_, label_ } = this.props;
    const { leftIcon, rightIcon } = Lock(iconName, this.handleLockClick);
    const { showPassword } = this.state;
    return (
      <Label>
        <span className="label-text">{label_}</span>
        <InputGroup
          placeholder={placeholder}
          className={class_}
          leftIcon={leftIcon}
          rightElement={rightIcon}
          type={showPassword ? 'text' : 'password'}
          large
        />
      </Label>
    );
  }
}
CustomInput.defaultProps = {
  class_: 'input',
};
CustomInput.propTypes = {
  placeholder: PropTypes.string.isRequired,
  iconName: PropTypes.string.isRequired,
  class_: PropTypes.string,
  label_: PropTypes.string.isRequired,
};
export default CustomInput;
