const EMAIL_REGEX = new RegExp('^(([^<>()[]\\.,;:s@"]+(.[^<>()[]\\.,;:s@"]+)*)|(".+"))@(([[0-9]{1,3}.[0-9]{1,3}.[0-9]{1,3}.[0-9]{1,3}])|(([a-zA-Z-0-9]+.)+[a-zA-Z]{2,}))$');

export default (value) => {
  console.log(value);

  if (EMAIL_REGEX.test(value)) {
    return true;
  }
  return false;
};
