import { UPDATE_CLASS_VALUE } from '../actions/types';

const initialState = {
  tasks: {},
  columns: {},
  columnOrder: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_CLASS_VALUE:
      return {
        ...state,
        [action.payload.schema]: { ...state[action.payload.schema], ...action.payload.data },
      };
    default:
      return state;
  }
};
