import React from 'react';
import { Button } from '@blueprintjs/core';
import client from '../../../../socket';

class Index extends React.Component {
  state={ apis: null };

  async componentWillMount() {
    const apisRes = await client.scope('Mentee');
    this.setState({ apis: apisRes });
    this.socketEventListner();
  }

  socketEventListner = () => {
    const test = client.on('createOffer', function CreateOffer(data) {
      console.log('create Offer is available in main socket', data, this);
    });
    // console.log('test', test);
  }

  createOfferHandler = () => {
    this.state.apis.createOffer({ userId: 3 });
  };

  render() {
    return (
      <div style={{ marginTop: 100 }}>
        <Button onClick={this.createOfferHandler} text="Invite user" />
      </div>
    );
  }
}
export default Index;
