import React from 'react';
import { Button } from '@blueprintjs/core';
import client from '../../../../socket';

class Index extends React.Component {
  state={ apis: null };

  async componentWillMount() {
    const apisRes = await client.scope('Mentee');
    this.setState({ apis: apisRes });
    this.socketEventListner();
  }

  socketEventListner = () => {
    client.on('createOffer', function CreateOffer(data) {
      console.log('create Offer is available in main socket', data, this);
    });
  }

  createOfferHandler = () => {
    const { apis } = this.state;
    apis.createOffer([{ sdp: { someData: 'test data' } }, [{ userId: 3 }]]);
  };

  render() {
    return (
      <div style={{ marginTop: 100, marginLeft: 100 }}>
        {/* eslint-disable-next-line */}
        <video playsInline autoPlay style={{ height: 300, width: 400, background: 'black'}} />
        <div style={{ marginTop: 20 }}>
          <Button onClick={this.startVideo} text="Start" />
          <Button onClick={this.remoteCall} text="Remote Call " />
          <Button onClick={this.createOfferHandler} text="Create Offer" />
          <Button onClick={this.createAnswer} text="Create Answer" />
          <Button onClick={this.addCandidate} text="Add Condidate" />
        </div>
      </div>
    );
  }
}
export default Index;
