const emailVal = new RegExp('^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})');

export default (value) => {
  if (emailVal.test(value)) {
    return true;
  }
  return false;
};
