import React, { Component } from 'react';
import PropTypes from 'prop-types';

class NewColumn extends Component {
  state = {};

  componentWillMount() {
    const { classState } = this.props;
    this.setState(classState);
  }

  onClick = () => {
    const { classState } = this.props;
    const { columns, columnOrder } = classState;
    const newColumn = {
      ...columns,
      'column-4': { id: 'column-4', title: 'New Column', taskIds: [] },
    };
    columnOrder.push('column-4');
    const newState = {
      ...classState,
      columnOrder: [...columnOrder],
      columns: [...newColumn],
    };
    this.setState(newState);
  };

  render() {
    console.log(this.state);
    return (
      <div className="add-new-column">
        <div className="title" onClick={this.onClick}>
          <span>+ New Column</span>
        </div>
      </div>
    );
  }
}

NewColumn.propTypes = {
  classState: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default NewColumn;
