import React from 'react';
import PropTypes from 'prop-types';
import { InputGroup, Icon, Label } from '@blueprintjs/core';
import { IconNames } from '@blueprintjs/icons';

const CustomInput = ({ placeholder, iconName, class_, label_ }) => {
  if (iconName === 'Search') {
    const icon = <Icon icon={IconNames.SEARCH} iconSize={Icon.SIZE_LARGE} />;
    return (
      <Label>
        <span className="label-text">{label_}</span>
        <InputGroup
          placeholder={placeholder}
          className={class_}
          leftIcon={icon}
          large
        />
      </Label>
    );
  } if (iconName === 'Lock') {
    const icon = <Icon icon={IconNames.LOCK} iconSize="15" style={{ top: '2px' }} />;
    return (
      <Label>
        <span className="label-text">{label_}</span>
        <InputGroup
          placeholder={placeholder}
          className={class_}
          rightElement={icon}
          large
        />
      </Label>
    );
  }

  return (
    <Label>
      <span className="label-text">{label_}</span>
      <InputGroup
        placeholder={placeholder}
        className={class_}
        large
      />
    </Label>
  );
};
CustomInput.defaultProps = {
  class_: 'input',
};
CustomInput.propTypes = {
  placeholder: PropTypes.string.isRequired,
  iconName: PropTypes.string.isRequired,
  class_: PropTypes.string,
  label_: PropTypes.string.isRequired,
};
export default CustomInput;
